package com.ReviewSession;

public class Class8TimeExersice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i <= 30; i++) {
            if (i < 10) {
                System.out.println("The time is 7:0" + i);

            } else {
                System.out.println("The time is 7:" + i);

            }	
		}
		
	
		
	}
		
}	
		
	


