package com.class7;

public class IncrementDecrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int x=1;
	
	x=x+1;
	
	System.out.println(x);
		
		
	int y=1;
	
	y+=1;
	System.out.println(y);
		
		
	int z=1;
	z++;
	System.out.println(z);
		
	int w=2;
	w++;
	System.out.println(w);
	
	int v=10;
	v--;
	System.out.println(v);
	
	
	System.out.println();
	
	int a=10;
	int b=++a;//pre-increment = first increment --> assign
	
    System.out.println("The value of b is " +b);//11
	
	System.out.println("The values of a is " +a);//11
	
	
	int c=10;
	int d=c++;//post-increment = first assign --> increment
	
	System.out.println("The value of d is " +d);
	
	System.out.println("The value of c is " +c);
	
	System.out.println();
	
	int q= 100;
	
	int e=q--;
	
	System.out.println("The value of q is " +e);
	System.out.println("The value of e is " +q);
	
	System.out.println();
	
	int h=50;
	int i=--h; //first decrement --> assign
	
	System.out.println("The value of h is " +h);
	System.out.println("The value of i is " +i);
	
	
	
	int j, k, l; 
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
		
		
		
	}

}
